package operators;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub {
		
		int i = 3;
		i++;  //incrementing 
		System.out.println(i);
		++i;
		System.out.println(i);
		System.out.println(++i);
		int j =3;
		--j; //decrementing
		int k = 3;
		++k;
		
		
		
		System.out.println(j);
		System.out.println(++k);
		
		int result = +1;
		System.out.println(result);
		System.out.println(++result);
		System.out.println(--result); 
		
		boolean x = false;
		System.out.println(x);
		System.out.println(!x);
	}}


